Blueprint make report.script:
{_ Function to obtain current inventory of a material}
{set _inventory(required) to:
    {for material in materials:
        {if material.material = required:
            {return material}
        }
    }
}

{_ Fetch blueprint from state }
{set blueprintname to state.blueprint}
{if blueprintname:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'blueprint')}
    {SetState('eddi_context_blueprint_name', blueprintname)}
}

{if !blueprint:
    {_ Fetch from context }
    {set blueprint to BlueprintDetails(state.eddi_context_blueprint_name)}
}

{set canbemade to 999}

{if blueprint:
    {_ Find the smallest number of this blueprint that can be made }
    
    {set cur to 0}
    {while cur < len(blueprint.materials):
        {set inventory to _inventory(blueprint.materials[cur].material)}
        {if !inventory:
            {set canbemade to 0}
        |else:
            {if inventory.amount < canbemade:
                {set canbemade to inventory.amount}
            }
        }
        {set cur to cur + 1}
    }

    {if canbemade = 0:
        No puede hacer ningún {blueprintname}
    |elif canbemade = 1:
        Solo puede hacer un {blueprintname}
    |else:
        Puede hacer {canbemade} {blueprintname}
    }
|else:
    No hay información disponible sobre los materiales necesarios para hacer {blueprintname}
}.

Blueprint material report.script:
{_ Fetch blueprint from state }
{set blueprintname to state.blueprint}
{if blueprintname:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'blueprint')}
    {SetState('eddi_context_blueprint_name', blueprintname)}
}

{if !blueprint:
    {_ Fetch from context }
    {set blueprint to BlueprintDetails(state.eddi_context_blueprint_name)}
}

{if blueprint:
    {set materials to len(blueprint.materials)}
    {blueprintname} requires 
    {if materials = 1:
        {blueprint.materials[0].material}
    |elif materials = 2:
        {blueprint.materials[0].material} and {blueprint.materials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(blueprint.materials):
            {if cur = 0:
                {blueprint.materials[cur].material}
            |elif cur < len(blueprint.materials) - 1:
                , {blueprint.materials[cur].material}
            |else:
                , and {blueprint.materials[cur].material}
            }
            {set cur to cur + 1}
        }
    }
|else:
    No hay información disponible sobre los materiales necesarios para hacer {blueprintname}
}.

Body report.script:
{_ Fetch from context }
{set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}

{if !reportbody.name || reportbody.name = "":
    No estoy segura sobre el cuerpo estelar acerca del cual me pregunta.
|else:
    {P(reportbody.name)} is a
    {if reportbody.gravity < 0.5:
        low-gravity
    |elif reportbody.gravity <2:
        medium-gravity
    |elif reportbody.gravity <4:
        high-gravity
    |else:
        extremely high-gravity
    }

    {if reportbody.tidallylocked:
        tidally-locked
    }

    {if reportbody.terraformstate = "Terraformable":
        terraformable
    |elif reportbody.terraformstate = "Terraformed":
        terraformed
    }

    {if len(reportbody.rings) > 0:
        ringed
    }

    {reportbody.planettype}

    {if reportbody.terraformstate = "Terraforming":
      in the process of being terraformed
    }

    that is

    {if reportbody.landable:
        suitable
    |else:
        unsuitable
    }
    for landing.

    {_ Atmosphere? }

    {_ Volcanism? }

    {if reportbody.landable :
        It has a
        {if reportbody.rotationalperiod <= 0.5:
            fast
        |elif reportbody.rotationalperiod > 20.0:
            slow
        }
        rotational period of {Humanise(reportbody.rotationalperiod)} days.

        {set goodmaterials to []}
        {set greatmaterials to []}
        {for material in reportbody.materials:
            {if material.percentage >= MaterialDetails(material.material).greatpctbody:
                {set greatmaterials to cat(greatmaterials, [material])}
            |elif material.percentage >= MaterialDetails(material.material).goodpctbody:
                {set goodmaterials to cat(goodmaterials, [material])}
            }
        }

        {if len(greatmaterials) > 0:
            This body contains very high levels of
            {if len(greatmaterials) = 1:
                {greatmaterials[0].material}
            |elif len(greatmaterials) = 2:
                {greatmaterials[0].material} and {greatmaterials[1].material}
            |else:
                {set cur to 0}
                {while cur < len(greatmaterials):
                    {if cur = 0:
                        {greatmaterials[cur].material}
                    |elif cur < len(greatmaterials) - 1:
                        , {greatmaterials[cur].material}
                    |else:
                        , and {greatmaterials[cur].material}
                    }
                    {set cur to cur + 1}
                }
            }
        }
        {if len(goodmaterials) > 0:
            {if len(greatmaterials) > 0:
                and high levels of
            |else:
                This body contains high levels of
            }
            {if len(goodmaterials) = 1:
                {goodmaterials[0].material}
            |elif len(goodmaterials) = 2:
