Commodity sale check.script:
{_ Function to find a given commodity in market}
{set market(commodity) to:
    {for marketcommodity in station.commodities:
        {if marketcommodity.EDDBID = commodity.EDDBID:
            {return marketcommodity}
        }
    }
}

{set totalprofit to 0}
{set profitablecommodities to []}
{set stolencommodities to []}
{set unknowncommodities to []}

{for cargo in ship.cargo:
    {if cargo.commodity.name != 'Limpet':
        {set marketcommodity to market(cargo.commodity)}
        {if !marketcommodity:
            {_ We have no market information for this commodity.  Add it to the list of unknowns }
            {set unknowncommodities to cat(unknowncommodities, [cargo.commodity])}
        }
        {set profit to (marketcommodity.sellprice - cargo.price) * cargo.amount}
        {if cargo.stolen = true:
            {set stolencommodities to cat(stolencommodities, [cargo.commodity])}
        |elif cargo.missionid:
            {_ don't do anything with mission cargo at the moment}
        |elif marketcommodity && profit > 0:
            {set profitablecommodities to cat(profitablecommodities, [cargo.commodity])}
            {set totalprofit to totalprofit + profit}
        }
    }
}

{_ Let the user know about any commodities they can sell here for a profit}
{if len(profitablecommodities) > 0:
    Puede vender su
    {if len(profitablecommodities) = 1:
        {SetState("altair_commodity", profitablecommodities[0].name)}
        {F("Altair commodities")} 
    |elif len(profitablecommodities) = 2:
        {SetState("altair_commodity", profitablecommodities[0].name)} {F("Altair commodities")} 
         and 
        {SetState("altair_commodity", profitablecommodities[1].name)} {F("Altair commodities")} 
    |else:
        {set cur to 0}
        {while cur < len(profitablecommodities):
            {if cur = 0:
                {SetState("altair_commodity", profitablecommodities[cur].name)} {F("Altair commodities")} 
            |elif cur < len(profitablecommodities) - 1:
                , {SetState("altair_commodity", profitablecommodities[cur].name)} {F("Altair commodities")}
            |else:
                , y {SetState("altair_commodity", profitablecommodities[cur].name)} {F("Altair commodities")}
            }
            {set cur to cur + 1}
        }
    }
    por un total de {totalprofit} créditos de beneficio.
}

{_ Let the user know about any commodities about which we are unsure if they can sell for a profit}
{if len(unknowncommodities) > 0:
    Podría {if len(profitablecommodities) > 0: además } vender su 
    {if len(unknowncommodities) = 1:
        {SetState("altair_commodity", unknowncommodities[0].name)} {F("Altair commodities")}
    |elif len(unknowncommodities) = 2:
        {SetState("altair_commodity", unknowncommodities[0].name)} {F("Altair commodities")}
        y 
        {SetState("altair_commodity", unknowncommodities[1].name)} {F("Altair commodities")}
    |else:
        {set cur to 0}
        {while cur < len(unknowncommodities):
            {if cur = 0:
                {SetState("altair_commodity", unknowncommodities[cur].name)} {F("Altair commodities")}
            |elif cur < len(profitablecommodities) - 1:
                , {SetState("altair_commodity", unknowncommodities[cur].name)} {F("Altair commodities")}
            |else:
                , y{SetState("altair_commodity", unknowncommodities[cur].name)} {F("Altair commodities")}
            }
            {set cur to cur + 1}
        }
    }
    , sin embargo compruebe beneficios potenciales {if len(unknowncommodities) > 1: de estos |else: de este}  antes de confirmar la transacción.
}

{_ Let the user know about any stolen commodities they can sell on the black market}
{if len(stolencommodities) > 0:
    {if station.hasblackmarket:
        Puede {OneOf('descargar', 'dump', 'fence')} su
        {if len(stolencommodities) = 1:
