            y
            {SetState("altair_commodity", stolencommodities[1].name)} {F("Altair commodities")}
        |else:
            {set cur to 0}
            {while cur < len(stolencommodities):
                {if cur = 0:
                    {SetState("altair_commodity", stolencommodities[cur].name)} {F("Altair commodities")}
                |elif cur < len(stolencommodities) - 1:
                    , {SetState("altair_commodity", stolencommodities[cur].name)} {F("Altair commodities")}
                |else:
                    , y {SetState("altair_commodity", stolencommodities[cur].name)} {F("Altair commodities")}
                }
                {set cur to cur + 1}
            }
        }
        robado aquí.
    |else:
        Recuerde:  {OneOf("tenemos cargamento robado a bordo", "llevamos mercancías robadas a bordo", "estamos transportando cargamento robado", "estamos transportando mercancías robadas")}.
