Community goal.script:
{_ Set Context _}
{SetState('eddi_context_last_subject', 'community goal')}
{_ Save goal location to context if there is only one goal present _}
{if len(event.cgid) = 1:
   {SetState('eddi_context_goal_system_name', event.system[0])}
   {SetState('eddi_context_goal_station_name', event.station[0])}
}

{F('Honorific')}, 

{if len(event.name) > 1:
    {OneOf("ha expresado su interés en", "estoy siguiendo")}
    los siguientes eventos de la comunidad:
|else: 
    El evento de la comunidad,    
}

{set cur to 0}
{while cur < len(event.name):

   {if cur = 0 && len(event.name) = 1:
      {_ nothing _}
   |elif cur = 0 && len(event.name) > 1:
      El primero,
   |elif cur < (len(event.name) - 1):
      el siguiente, 
   |else:
      el último,
   }

   {event.name[cur]},

   {if event.iscomplete[cur]:
      {OneOf("está completado", "ha finalizado")} 
   |else: 
      {if (event.expiry[cur] / 3600) < 12:
         {OneOf("está a punto de terminar", 
                "terminará en {round(event.expiry[cur] / 3600, 1)} horas", 
                "está casi completado")}
      |else:
         {OneOf("no se ha completado aun", 
                "está activo", 
                "está incompleto")} 
      }
   }

   {if event.tier[cur]:
       {OneOf("y ha alcanzado", "y está actualmente en")} {event.tier[cur]}.
       {if event.contribution[cur] > 0:
           Su recompensa asignada está
           {Occasionally(2, " actualmente ")}
           {Occasionally(2, "calculada ")}
           en {event.tierreward[cur]} créditos
           {Occasionally(2, "en el {event.percentileband[cur]} por ciento")}
       }
   }.

   {_ Create a comparison function _}
   {set compare(stat) to:     
       {if stat < 0.01:
            solo una fracción de            
       |elif stat < 0.10:
            menos de un décimo de 
       |elif stat < 0.25:
            menos de un cuarto de
       |elif stat < 0.5:
            menos de la mitad de
       |elif stat < 0.75:
            menos de
       |elif stat < 0.96: 
            {OneOf("apenas un poco","ligeramente")}
            menos de
       |elif stat < 1.05:
            {OneOf("aproximádamente","casi")} 
            igual a 
       |elif stat < 1.25:
            un poco más de 
       |elif stat < 1.5:
            más de 
       |elif stat < 2:
            significativamente más de
       |else:
            bastante más de
       }
    }
    {set compare_contribution to (event.contribution[cur] / round(event.total[cur] / event.contributors[cur], 0))}

    {if compare_contribution != 0:
       {if event.toprank[cur]: 
          Enhorabuena! 
          Es usted uno de los {event.topranksize[cur]} mejores comandantes en este evento, habiendo contribuido con 
          {compare(compare_contribution)}
          la media.
       |else:
          {Occasionally(2, "Ha contribuido con 
          {compare(compare_contribution)}
          la media.")}
       }
    }

    {if !event.iscomplete[cur]:
       Puede 
       {if event.contribution[cur] > 0: 
          {OneOf("contribuir más", "hacer contribuciones adicionales")} 
       |else: 
          {OneOf("contribuir", "hacer una contribución")}
       }
       {if event.station[cur] = station.name:
          {Occasionally(2, "aquí")}
          {Occasionally(2, "en esta estación")}.
       |elif event.system[cur] = system.name:
          en {event.station[cur]}
          {Occasionally(2, "aquí")}
          {Occasionally(2, "en este sistema", "en el sistema donde nos encontramos")}.
       |else:
          en {event.station[cur]},
          en {OneOf("{event.system[cur]}", "el sistema {event.system[cur]}" )}.
       }
    }

    {set cur to cur + 1}
    {Pause(1000)}
}

