FSD engaged.script:
{_ Context }
{SetState('eddi_context_last_subject', 'jump')}
{SetState('eddi_context_last_action', 'start')}
{SetState('eddi_context_system_name', system.name)}
{SetState('eddi_context_system_system', system.name)}

{if event.target = 'Hyperspace':
    {_ Rewrite context to represent a jump }
    {SetState('eddi_context_last_subject', 'jump')}
    {SetState('eddi_context_last_action', 'start')}

    {set system to SystemDetails(event.system)}

    {_ Wait for the FSD countdown and initial jump}
    {Pause(6000)}

    {OneOf("Destino confirmado","Salto en progreso","Telemetría obtenida")}.
    {OneOf("nos dirigimos a", "en ruta al sistema", "viajamos a", "en tránsito hacia")} {system.name}.
    {if system.name = homesystem.name:
        Welcome home, {F("Honorific")}
    |else:
        {set scoopables to ["O", "B", "A", "F", "G", "K", "M"]}
        {if slice(event.stellarclass, 0, 1) = "D":
            Precaución: Enana blanca detectada.
        |elif event.stellarclass = "N":
            Precaución: estrella de neutrones detectada.
        |elif event.stellarclass = "H":
            Precaución:  agujero negro detectado.
        |elif find(scoopables, event.stellarclass) = -1:
            Información: la estrella destino no es adecuada para repostar.
        }

        {if lastsystem.allegiance && lastsystem.allegiance != "None" && lastsystem.allegiance != "Independent" && (!system.allegiance || system.allegiance = "None" || system.allegiance = "Independent"):
            {OneOf("estamos abandonando el", "hemos dejado el", "ya no estamos en")} {OneOf("territorio","espacio")} 
            {if lastsystem.allegiance = "Empire":
              {OneOf("del imperio","imperial", "controlado por el imperio")} 
            |elif lastsystem.allegiance = "Federation":   
              {OneOf("de la federación","federal","controlado por la federación")} 
            |elif lastsystem.allegiance = "Alliance":
              {OneOf("de la alianza","controlado por la alianza")} 
        }.
        |elif lastsystem.allegiance && lastsystem.allegiance != system.allegiance && system.allegiance && system.allegiance != "None":
            {OneOf("Ahora estamos en", "hemos entrado en", "estamos entrando en el")} {OneOf("territorio","espacio")}
            {if system.allegiance = "Empire":
              {OneOf("del imperio","imperial", "controlado por el imperio")} 
            |elif system.allegiance = "Federation":   
              {OneOf("de la federación","federal","controlado por la federación")}
            |elif system.allegiance = "Alliance":   
              {OneOf("de la alianza","controlado por la alianza")}
            |elif system.allegiance = "Independent":   
              independiente
            }.
            {if cmdr.title != "Commander":
                Bienvenido de nuevo {F("Honorific")}.
            }
        }

        {if system.visits = 0:
            Es nuestra primera visita a este sistema
        |elif system.visits = 1:
            Es nuestra segunda visita a este sistema
        |elif system.visits = 2:
            Es nuestra tercera visita a este sistema
        |elif system.visits = 3:
            Es nuestra cuarta visita a este sistema
        |else:
            Hemos estado {system.visits} veces en este sistem
        }.

        {if !system.population:
            {OneOf("Este sistema está despoblado","No hay presencia humana aquí", "La raza humana no ha colonizado aun este sistema")}.
        |else:
            {F("System report")}
        }

        {if system.distancefromhome:
            {Occasionally(7, "{OneOf(\"Estamos a\", \"NUestra posición actual está a\", \"Ahora estamos a\")} {system.distancefromhome} años luz de {OneOf(\"casa\", \"{homesystem.name}\")}.")}
        }

        {if system.comment:
            Tiene {OneOf("establecida una nota", "establecido un comentario", "establecida una reseña")} {OneOf("sobre", "acerca de", "en")} este sistema.  {OneOf("dice lo siguiente", "pone lo siguiente", "es la siguiente","dice así")}, {system.comment}.
        }
    }
}

