Galnet news published.script:
{set statustitle to "^(Galactic News: ((Weekly .*)|(Starport Status Update)))|(Powerplay: Incoming Update)"}

{if len(event.items) = 1:
    Se ha publicado un nuevo artículo en Galnet titulado {event.items[0].title}.
    {if !match(event.items[0].title, statustitle):
      Dice así: {event.items[0].content}.
    }
|elif len(event.items) > 1:
    Se han publicado {len(event.items)} nuevos artículos en Galnet.
    {set item to 0}
    {while item < len(event.items):
        {if item = 0:
            El primero se titula {event.items[item].title}
            {if !match(event.items[item].title, statustitle):
                , y dice así: {event.items[item].content}
            }.
        |elif item = 1:
            El segundo se titula {event.items[item].title}
            {if !match(event.items[item].title, statustitle):
                , y dice así: {event.items[item].content}
            }.
        |elif item = len(event.items) - 1:
            El último se titula {event.items[item].title}
            {if !match(event.items[item].title, statustitle):
                , y dice así: {event.items[item].content}
            }.
        |else:
            El siguiente se titula {event.items[item].title}
            {if !match(event.items[item].title, statustitle):
                , y dice así: {event.items[item].content}
            }.
        }
        {set item to item + 1}
        {Pause(1000)}
    }
}

