}

{if !material:
    {_ Fetch from context }
    {set material to MaterialDetails(state.eddi_context_material_name)}
}

{set inventory to _inventory(material.name)}


{if inventory:
    {if inventory.maximum && inventory.maximum < inventory.amount:
        {set over to inventory.amount - inventory.maximum}
        {if over > 1:
            Puede deshacerse de {over} unidades de {SetState("altair_material", inventory.material)}
            {F("Altair materials")} para alcanzar su máximo nivel.
        |elif over = 1:
            Puede deshacerse de 1 unidad de {SetState("altair_material", inventory.material)}
            {F("Altair materials")} para alcanzar su máximo nivel.
        }
    |elif inventory.desired && inventory.desired < inventory.amount:
        {set over to inventory.amount - inventory.desired}
        {if over > 1:
            Puede deshacerse de {over} unidades de {SetState("altair_material", inventory.material)}
            {F("Altair materials")} para alcanzar el nivel deseado.
        |elif over = 1:
            Puede deshacerse de 1 unidad de {SetState("altair_material", inventory.material)}
            {F("Altair materials")} para alcanzar el nivel deseado.
        }
    |elif inventory.desired:
        Está por debajo del nivel deseado de {SetState("altair_material", inventory.material)}
            {F("Altair materials")}.
    |elif inventory.maximum:
        Está por debajo del nivel máximo de {SetState("altair_material", inventory.material)}
            {F("Altair materials")}.
    |else:
        No ha fijado ningún nivel para {SetState("altair_material", inventory.material)}
            {F("Altair materials")}.
    }
}

