    La distancia hasta {reportsystem.name} es de {Distance(reportsystem.x, reportsystem.y, reportsystem.z, system.x, system.y, system.z)} años luz.
}

Touchdown.script:
{set body to BodyDetails(state.eddi_context_body_name, state.eddi_context_system_name)}

{if event.playercontrolled = true:
  Hemos aterrizado {if body.name: en{body.name} }.
|else:
  {ShipName()} ha aterrizado
  {if body.name: en {body.name} }.
}


{Pause(2000)}

{if type(event.latitude) != "void" && type(event.longitude) != "void" :
   Las coordenadas son  {round(event.latitude, 2)} grados de latitud, {round(event.longitude, 2)} grados de longitud.
}

Trade data purchased.script:
{_ Context }
{SetState('eddi_context_last_subject', 'tradedata')}
{SetState('eddi_context_last_action', 'buy')}
{SetState('eddi_context_tradedata_name', event.system)}

{OneOf("Adquiridos datos de comercio de {event.system}",
            "Datos comerciales de {event.system} disponibles","Datos comerciales de {event.system} recibidos correctamente"
            "Recibidos datos comerciales de {event.system} desde los servicios de la estación",
            "Recepción de datos comerciales de {event.system} confirmada")}.

Trade voucher redeemed.script:
{_ Context }
{SetState('eddi_context_last_subject', 'trade_voucher')}
{SetState('eddi_context_last_action', 'redeem')}
{SetState('eddi_context_trade_voucher_amount', event.amount)}

Devengados {event.amount} créditos en vales de comercio
{Occasionally(2, "
  {if len(event.rewards) = 1:
    por parte de la facción {event.rewards[0].faction}
  |else:
    por parte de 
    {set cur to 0}
    {while cur < len(event.rewards):
        {if cur = 0:
        |elif cur < len(event.rewards) - 1:
            , 
        |else:
            y
        }
        {event.rewards[cur].faction}
        {set cur to cur + 1}
    }
  }
")}.

